		rec.subscriptiondescription = current.variables.subscriptionDescription;
		rec.businessinformation = current.variables.BusinessInformation;
		rec.securityzone = current.variables.securityzone;
		if(current.variable_pool.u_hwpreset = "custom")
			{
			rec.hardwarepreset = current.variable_pool.u_hwpreset;	
			}
		else{
			rec.hardwarepreset = workflow.scratchpad.HardwarePreset;
		}
		rec.active = true;
	
		workflow.info(current.variable_pool.u_hwpreset);
		workflow.info(workflow.scratchpad.HardwarePreset);		
		workflow.info('Variables are updated now todating row');
		rec.update();
		}
	}
	catch(err){
		workflow.info('Error encounter while updation CMDB' + err); 
	}
	
	
}

insertRecord();
setClosed();

function setClosed() {
	var req = new GlideRecord('sc_req_item');
	req.addQuery('sys_id', current.getValue('sys_id'));
	req.query(); 
	while (req.next()) { 
	 req.state = '3';
	 req.update();
	}
}


