insertRecord();
setClosed();

function setClosed() {
	var req = new GlideRecord('sc_req_item');
	req.addQuery('sys_id', current.getValue('sys_id'));
	req.query(); 
	while (req.next()) { 
	 req.state = '3';
	 req.update();
	}
}


function insertRecord(){
			var get_vm_info_flow_status_activity_no = 19;
			var responseBodystr = data.get(get_vm_info_flow_status_activity_no).body;
			var responsebody = JSON.parse(responseBodystr);
			var InstancePropertiesString = responsebody.flowOutput.InstanceProperties;
			workflow.info('InstancePropertiesString before replace : ' + InstancePropertiesString); 
			InstancePropertiesString = InstancePropertiesString.replace(/\\n/g, "");
			InstancePropertiesString = InstancePropertiesString.replace(/\\/g, "");
			var InstanceProperties = JSON.parse(InstancePropertiesString);
			workflow.info('InstancePropertiesString after parsing : ' + InstancePropertiesString);
			workflow.info('InstanceProperties after parsing : ' + InstanceProperties);
	
			workflow.scratchpad.ActionTaken = InstanceProperties.ActionTaken  != null ? InstanceProperties.ActionTaken : "Deployed";
			workflow.scratchpad.BackupType = InstanceProperties.BackupType != null ? InstanceProperties.BackupType : current.variable_pool.BackupType;
			workflow.scratchpad.CUSTOMER = InstanceProperties.CUSTOMER != null ? InstanceProperties.CUSTOMER : "None";
			workflow.scratchpad.DeployStartDateTime = InstanceProperties.DeployStartDateTime != null ? InstanceProperties.DeployStartDateTime : "None";
			workflow.scratchpad.Function = InstanceProperties.Function != null ? InstanceProperties.Function : "None";
			workflow.scratchpad.HardwarePreset = InstanceProperties.HardwarePreset != null ? InstanceProperties.HardwarePreset : "None";
			workflow.scratchpad.HostEnvironment = InstanceProperties.HostEnvironment != null ? InstanceProperties.HostEnvironment : "None";
			workflow.scratchpad.SiteID = InstanceProperties.SiteID != null ? InstanceProperties.SiteID : "None";
			workflow.scratchpad.SubscriptionID = InstanceProperties.SubscriptionID != null ? InstanceProperties.SubscriptionID : "None";
			
			workflow.scratchpad.disk0Datastore = InstanceProperties.disk0Datastore != null ? InstanceProperties.disk0Datastore : "None";
			workflow.scratchpad.disk0SizeGB = InstanceProperties.disk0SizeGB != null ? InstanceProperties.disk0SizeGB : "None";
			workflow.scratchpad.disk0StorageTier = InstanceProperties.disk0StorageTier != null ? InstanceProperties.disk0StorageTier : "None";
			workflow.scratchpad.disk1SizeGB = InstanceProperties.disk1SizeGB != null ? InstanceProperties.disk1SizeGB : "0";
			workflow.scratchpad.disk2SizeGB = InstanceProperties.disk2SizeGB != null ? InstanceProperties.disk2SizeGB : "0";
			workflow.scratchpad.disk3SizeGB = InstanceProperties.disk3SizeGB != null ? InstanceProperties.disk3SizeGB : "0";
			workflow.scratchpad.disk4SizeGB = InstanceProperties.disk4SizeGB != null ? InstanceProperties.disk4SizeGB : "0";
			workflow.scratchpad.disk5SizeGB = InstanceProperties.disk5SizeGB != null ? InstanceProperties.disk5SizeGB : "0";
			workflow.scratchpad.disk6SizeGB = InstanceProperties.disk6SizeGB != null ? InstanceProperties.disk6SizeGB : "0";
