insertRecord();
setClosed();

function setClosed() {
	var req = new GlideRecord('sc_req_item');
	req.addQuery('sys_id', current.getValue('sys_id'));
	req.query(); 
	while (req.next()) { 
	 req.state = '3';
	 req.update();
	}
}


