insertRecord();
setClosed();

function setClosed() {
	var req = new GlideRecord('sc_req_item');
	req.addQuery('sys_id', current.getValue('sys_id'));
	req.query(); 
	while (req.next()) { 
	 req.state = '3';
	 req.update();
	}
}


function insertRecord(){
			
	var get_vm_info_flow_status_activity_no = 19;
	var responseBodystr = data.get(get_vm_info_flow_status_activity_no).body;
	var responsebody = JSON.parse(responseBodystr);
	var InstancePropertiesString = responsebody.flowOutput.InstanceProperties;
	workflow.info('InstancePropertiesString before replace 1 : ' + InstanceProperties); 
	workflow.info('InstancePropertiesString before replace : ' + InstancePropertiesString); 
	InstancePropertiesString = InstancePropertiesString.replace(/\\n/g, "");
	InstancePropertiesString = InstancePropertiesString.replace(/\\/g, "");
	var InstanceProperties = JSON.parse(InstancePropertiesString);
	workflow.info('InstancePropertiesString after parsing' + InstancePropertiesString); 
workflow.scratchpad.hostname_lc = InstanceProperties.hostname_lc;
	workflow.scratchpad.ActionTaken = InstanceProperties.ActionTaken  != null ? InstanceProperties.ActionTaken : "Decommisioned";
	
	var OSName = current.variable_pool.osName;
	var rec;
	try{
		workflow.info('Looking Records For :' + workflow.scratchpad.hostname_lc);
		rec = new GlideRecord('x_58872_needit_uvcore_physical_server');
		rec.addQuery('hostname_lc',workflow.scratchpad.hostname_lc);
		rec.query();

		workflow.info(rec.getRowCount() + ' Records Found :' + rec);
	
	while(rec.next()) {
		workflow.info(workflow.scratchpad.ActionTaken);
		rec.actiontaken = workflow.scratchpad.ActionTaken;
	    rec.subscription_status = "Decommissioned";
		workflow.info('before db update:');
		rec.update();
		}
	}
	catch(err){
		workflow.info('Error encounter while updation CMDB' + err); 
	}
	
	
}

