			var InstancePropertiesString = responsebody.flowOutput.InstanceProperties;
			workflow.info('InstancePropertiesString before replace : ' + InstancePropertiesString); 
			InstancePropertiesString = InstancePropertiesString.replace(/\\n/g, "");
			InstancePropertiesString = InstancePropertiesString.replace(/\\/g, "");
			var InstanceProperties = JSON.parse(InstancePropertiesString);
			workflow.info('InstancePropertiesString after parsing : ' + InstancePropertiesString);
			workflow.info('InstanceProperties after parsing : ' + InstanceProperties);
	
			workflow.scratchpad.ActionTaken = InstanceProperties.ActionTaken  != null ? InstanceProperties.ActionTaken : "Deployed";
			//workflow.scratchpad.BackupType = InstanceProperties.BackupType != null ? InstanceProperties.BackupType : current.variable_pool.BackupType;
			//workflow.scratchpad.backupPolicy = InstanceProperties.backupPolicy != null ? InstanceProperties.backupPolicy : current.variable_pool.backupPolicy;
			//workflow.scratchpad.backupMethod = InstanceProperties.backupMethod != null ? InstanceProperties.backupMethod : current.variable_pool.backupMethod;
			//workflow.scratchpad.backupWindow = InstanceProperties.backupWindow != null ? InstanceProperties.backupWindow : current.variable_pool.backupWindow;
			workflow.scratchpad.BillingCode = InstanceProperties.BillingCode != null ? InstanceProperties.BillingCode : "123";
