function insertRecord(){
	var ins;
	ins = new GlideRecord('x_58872_needit_uvcore_lb');
	ins.addQuery('hostname_lc',current.variables.hostname_lc);
	ins.query();
	while(ins.next()){
	ins.decommision_date = GlideDateTime().getDisplayValue();
	ins.mark_delete = true;
	ins.update();
	activity.result = 'False';
	}
}
var rec;
