rec.addQuery('hostname_lc',current.variables.hostname_lc);
rec.query();

while(rec.next()){
	
	var mark = rec.getValue('mark_delete');
	workflow.info('mark' + mark);
	
	var curr_date = new GlideDateTime();
	workflow.info(curr_date);
	
if(mark == 1)
{
		
		var decom_date = new GlideDateTime(rec.getValue('decommision_date'));
		workflow.info(decom_date);
		var diffSeconds = GlideDateTime.subtract(decom_date,curr_date).getDayPart();
		workflow.info(diffSeconds);
		
		if(diffSeconds > '7')
		{
			activity.result = 'True';
		}
		else
			{
			activity.result = 'False';
		}
}
else
{
	insertRecord();
}
}

