//var field = [];
var field = current.variables.subscription_name.toString();
var fields = field.split('+');
workflow.scratchpad.subscription_name = fields[0];


workflow.info(workflow.scratchpad.subscription_name);

if(current.variables.phase == "Deploy"){
	workflow.scratchpad.reference = current.sys_id;
} else if(current.variables.phase == "Modify" || current.variables.phase == 'Decommission' || current.variables.phase == 'Action'){
	
	// Saving refrence for future ref
	workflow.scratchpad.reference = current.variables.reference;
	
	
	workflow.info('Looking Records For :' + current.variables.hostname_lc);
	rec = new GlideRecord('x_58872_needit_uvcore_server');
	rec.addQuery('hostname_lc',current.variables.hostname_lc);
	rec.query();

	workflow.info(rec.getRowCount() + ' Records Found :' + rec);
	
	while(rec.next()) {
		
		rec.active = false;
		rec.update();
		
	}
}

if(current.variables.enableBackup == "Yes"){
	workflow.scratchpad.enableBackup = "true";
}else{
	workflow.scratchpad.enableBackup = "false";
}

if(current.variables.enableSRM == "Yes"){
	workflow.scratchpad.enableSRM = "true";
}else{
	workflow.scratchpad.enableSRM = "false";
}


if(current.variables.u_hwpreset != "custom")
	{
		workflow.scratchpad.hardware_preset = current.variables.u_hwpreset;
		workflow.scratchpad.CPUcount = "#NULL#";
		workflow.scratchpad.MemoryGB = "#NULL#";
		//workflow.scratchpad.disk0_size = "#NULL#";
	}
else
	{
		workflow.scratchpad.hardware_preset = current.variables.u_hwpreset;
